var m = {
  jmap: {
    unzip: function(j, map) {
      var f = {}, i = 0, c = j.length;
      while (i < c) {
        f[map[i]] = j[i];
        i+=1;
      }
      c = map.length;
      while (i < c) f[map[i++]] = '';

      return f;
    },
    zip: function(j, map){
      var c = [], n = map.length, i=0;
      while (i<n) {
        c.push(j[map[i]] || '');
        i+=1;
      }
      return c;
    },
    s_zip: function(j, map){
      return JSON.stringify(this.zip(j, map));
    },
    s_unzip: function(str, map){
      return this.unzip(JSON.parse(str), map);
    }
  }
};
