m.zipcookie = (function(){
  var lstor = window.localStorage;
  //lstor.clear();

  return {
    get: function(name, map){
      var item = lstor.getItem(name);
      if (item) return m.jmap.s_unzip(item, map);
      else return null;
    },
    set: function(name, j, map){
      lstor.setItem(name, m.jmap.s_zip(j, map));
    }
  };
})();
